mapper.select:
select * from emp


        name like concat('%', #{name}, '%')


        and gender = #{gender}


        and entrydate between #{begin} and #{end}


order by update_time desc
limit #{start}, #{pageSize}

mapper.update:
update emp

    username = #{username}, name = #{name}, gender = #{gender}

        ,image = #{image}


        ,dept_id = #{deptId}


        ,job = #{job}


        ,entrydate = #{entrydate}


where id = #{id}
